package javapractice;
import java.util.Scanner;
public class Q2739gugudan {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int a = sc.nextInt();
		for(int i=1; i<10; i++) {
			System.out.println(a+" * "+i+" = "+a*i);
		}
	}

}
